import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

function HeroSection() {
    const settings = {
        dots: false,
        infinite: true,
        speed: 1000,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 5000,
        arrows: false,
        fade: true,
        cssEase: 'linear'
    };

    const images = [
        'https://images.unsplash.com/photo-1507525428034-b723cf961d3e?q=80&w=1920&auto=format&fit=crop',
        'https://images.unsplash.com/photo-1519046904884-53103b34b206?q=80&w=1920&auto=format&fit=crop',
        'https://images.unsplash.com/photo-1507525428034-b723cf961d3e?q=80&w=1920&auto=format&fit=crop',
        'https://images.unsplash.com/photo-1519046904884-53103b34b206?q=80&w=1920&auto=format&fit=crop'
    ];

    return (
        <section id="home" className="h-screen flex items-center justify-center relative overflow-hidden">
            <div className="absolute inset-0 w-full h-full">
                <Slider {...settings} className="h-full">
                    {images.map((image, index) => (
                        <div key={index} className="h-screen">
                            <div 
                                className="h-screen w-full bg-cover bg-center"
                                style={{ 
                                    backgroundImage: `url(${image})`,
                                    height: '100vh'
                                }}
                            />
                        </div>
                    ))}
                </Slider>
            </div>
            <div className="absolute inset-0 bg-black bg-opacity-40"></div>
            <div className="relative text-center max-w-3xl mx-auto px-4 bg-black bg-opacity-60 py-8 rounded-lg z-10">
                <h2 className="text-5xl font-extrabold mb-4">Devenez plus fort avec Ange</h2>
                <p className="text-lg mb-6">Coaching en musculation personnalis√© pour sculpter votre corps, gagner en force et atteindre vos objectifs fitness.</p>
                <a href="#contact" className="bg-yellow-500 text-black px-6 py-3 rounded-full font-semibold hover:bg-yellow-400 transition">Commencer maintenant</a>
            </div>
        </section>
    );
}

export default HeroSection;
